package vsoc;import vsoc.nn.base.Params;import vsoc.nn.base.RandomValue;import vsoc.nn.base.RandomWgt;import junit.framework.TestCase;/** * Testcases. */public class RandomTest extends TestCase {    RandomValue rv;    RandomWgt rw;    public RandomTest(String s) {        super(s);    }    protected void setUp() {        this.rv = new RandomValue();        this.rw = new RandomWgt();    }    public void testValueMinMax() {        int i, n;        short max, min, val;        n = 1000;        max = 0;        min = Params.maxValue;        for (i = 1; i <= n; i++) {            val = this.rv.nextValue();            if (val < min)                min = val;            if (val > max)                max = val;        }        assertTrue(min == 0);        assertTrue(max == Params.maxValue - 1);    }    public void testWeightMinMax() {        int i, n;        short max, min, val;        n = 1000;        max = 0;        min = Params.maxWgt;        for (i = 1; i <= n; i++) {            val = this.rw.nextValue();            if (val < min)                min = val;            if (val > max)                max = val;        }        assertTrue(min == -Params.maxWgt + 1);        assertTrue(max == Params.maxWgt - 1);    }}