package vsoc.genetic;/*** Interface for Objects that have the capability to genereate Objects of the same* type by applying genetic operations to parent objects.*/public interface Crossable<T extends Crossable<?>> {    /**    * Creates a new instance of a new crossable object by applying    * crossover and mutation on two parent objects.    */    public T newChild(T otherParent, double mutationRate);    public void setWeightsRandom(long seed);    }