package vsoc.nn.base;import java.io.FileWriter;import java.io.IOException;import java.io.Writer;public class WeighterTest extends Object {    public static void main(String[] arg) {        try {            Weighter wgt = new Weighter((float) 0.1, (float) 3.0);            Writer w = new FileWriter("Weighter.txt");            write(wgt, w);            w.close();            System.out.print("Results in: Weighter.txt");        } catch (Exception e) {            e.printStackTrace();        }    }    private static void write(Weighter weighter, Writer w) throws IOException {        short val, wgt, ival, iwgt, maxVal, maxWgt;        int result;        w.write("val\twgt\tresult\n");        maxVal = weighter.maxValue();        maxWgt = weighter.maxWeight();        for (ival = (short) (0); ival < maxVal; ival++) {            for (iwgt = (short) (-maxWgt + 1); iwgt < maxWgt; iwgt++) {                result = weighter.getWeightedValue(ival, iwgt);                w.write(new Short(ival).toString());                w.write("\t");                w.write(new Short(iwgt).toString());                w.write("\t");                w.write(new Integer(result).toString());                w.write("\n");            }        }    }}