package vsoc.test;import java.io.File;import java.io.FileWriter;import java.io.IOException;import java.io.Writer;import vsoc.nn.base.TransferManager;public class TransferTest {    public static void main(String[] arg) {        try {            Writer w = new FileWriter("Transfer.txt");            writeOptimalStretch(w);            w.close();            File f = new File("Transfer.txt");            System.out.print("Results in: " + f.getAbsolutePath());        } catch (Exception e) {            e.printStackTrace();        }    }    private static void writeOptimalStretch(Writer w) throws IOException {        w.write("syn\topt\n");        for (int i = 1; i < 500; i += 1) {            w.write("" + i + "\t" + TransferManager.optimalStretch(i) + "\n");        }    }}